package com.examples;

public class MyBoolean1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean testResult;
		
		testResult=false;
		System.out.println("Current value of restResult: "+testResult);
		
		testResult=true;
		System.out.println("Current value of restResult: "+testResult);
		
		testResult=!testResult;
		System.out.println("Current value of restResult: "+testResult);
		
		// = assign
		// == equal to
		testResult=1==2;
		System.out.println("Current value of restResult: "+testResult);

	}

}
