package com.examples;

public class MyStr1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myStrVar;
		
		myStrVar="Quick Brown";
		System.out.println("myStrVar contains: "+myStrVar);
		
		myStrVar=myStrVar+" Fox Jumps";
		System.out.println("myStrVar contains: "+myStrVar);
		
		myStrVar+=" the Lazy";
		System.out.println("myStrVar contains: "+myStrVar);
		
		myStrVar+=" Dog";
		System.out.println("myStrVar contains: "+myStrVar);
	}

}
